mapper.select:
SELECT A.*, (select count(*) from register where pay_st = 0 and reg_st = 0 and ep_no = A.EP_NO) REGISTER_STUDENT_COUNT FROM ( SELECT SCHDL_TIME, EP_NO, EP_COUNT, CLASS_MAX_PERSON - CLASS_PERSON "REMAIN_PEOPLE_CNT", EP_PRICE, TO_CHAR(SCHDL_DT, 'YYYY"년" MM"월" DD"일' ) SCHDL_DT, SCHDL_WEEK, SCHDL_START_TIME, SCHDL_END_TIME, (SELECT COUNT(*) FROM EP_SCHEDULE WHERE SCHDL_NO = ES.SCHDL_NO AND SCHDL_DT > SYSDATE) "POSSIBLE_FL" FROM CLASS JOIN EPISODE USING(CLASS_NO) JOIN EP_SCHEDULE ES USING(EP_NO) WHERE CLASS_NO = #{classNo}
AND EP_COUNT = ${epCount}
ORDER BY SCHDL_DT, TO_DATE(SCHDL_START_TIME, 'HH24:MI') ) A

SELECT TO_CHAR(SCHDL_DT, 'YYYY-MM-DD ' ) ||  SCHDL_START_TIME SCHDL_DT FROM CLASS 
JOIN EPISODE USING(CLASS_NO)
JOIN EP_SCHEDULE USING(EP_NO)
JOIN REGISTER R USING(EP_NO)
WHERE CLASS_NO = ${classNo}
AND R.MEMBER_NO = ${memberNo}
